'''This program will eventually run all cs projects I have done in python.'''import sys, ossys.path.append(os.getcwd()+'/src/pythonBasics/')sys.path.append(os.getcwd()+'/src/advancedPython/')sys.path.append(os.getcwd()+'/src/dataProcessing/src/')sys.path.append(os.getcwd()+'/src/juliaMandelbrot/src/')import basicPasswordValidator, cardGame, TestCheckSodokuSolution, circles, coinFlipimport creditCardValidator, diceGame, drawOlympicRings, drawRectangle, estimatePiimport findPerfectNumbers, fourInARow, histogram, investmentCalculatorimport lotteryGame, palindromeChecker, payroll, python_text_toolsimport recursiveWebCrawler, rockPaperScissors, ShowCurrentTime, stopwatchimport TestCheckSodokuSolution, tkinterPractice, turtleWalk, BingobasicSwitcher = {    1: basicPasswordValidator,    2: cardGame,    3: TestCheckSodokuSolution,    4: circles,    5: coinFlip,    6: creditCardValidator,    7: diceGame,    8: drawOlympicRings,    9: drawRectangle,    10: estimatePi,    11: findPerfectNumbers,    12: fourInARow,    13: histogram,    14: investmentCalculator,    15: lotteryGame,    16: palindromeChecker,    17: payroll,    18: python_text_tools,    19: recursiveWebCrawler,    20: rockPaperScissors,    21: ShowCurrentTime,    22: stopwatch,    23: TestCheckSodokuSolution,    24: tkinterPractice,    25: turtleWalk,    26: Bingo,}def chooseProgram(p):    # Get the function from switcher dictionary    func = basicSwitcher.get(p, lambda: "Invalid program name.")    # Execute the function    func.run()if __name__ == "__main__":    print("Pick a program from the list below by entering the corresponding number.\n")    for k, v in basicSwitcher.items():        print("{}: {}".format(k ,v.__name__))    userProgram = int(input("Select program: "))    chooseProgram(userProgram)